import { useState } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification,
  sendPasswordResetEmail,
  signInWithPopup,
  signOut,
} from "firebase/auth";
import { auth, googleProvider, db } from "./firebase";
import { doc, setDoc } from "firebase/firestore";

function AuthTest() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [signupEmail, setSignupEmail] = useState("");
  const [signupPassword, setSignupPassword] = useState("");
  const [otpEmail, setOtpEmail] = useState("");

  const [message, setMessage] = useState(null);

  const [loginLoading, setLoginLoading] = useState(false);
  const [googleLoading, setGoogleLoading] = useState(false);
  const [signupLoading, setSignupLoading] = useState(false);
  const [forgotLoading, setForgotLoading] = useState(false);

  const [rememberMe, setRememberMe] = useState(false);
  const [signupPopup, setSignupPopup] = useState(false);
  const [forgotPasswordPopup, setForgotPasswordPopup] = useState(false);

  const showMessage = (text, type = "success") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  // ðŸ”¹ LOGIN
  const login = async () => {
    if (!email || !password) {
      showMessage("Enter email & password", "error");
      return;
    }

    setLoginLoading(true);
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      const user = result.user;

      if (!user.emailVerified) {
        showMessage("ðŸ” Please verify your email first!", "error");
        await signOut(auth);
        return;
      }

      if (rememberMe) localStorage.setItem("rememberedUser", user.uid);

      showMessage(`âœ… Welcome back, ${user.email}`);
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setLoginLoading(false);
    }
  };

  // ðŸ”¹ SIGNUP + EMAIL VERIFICATION
  const signup = async () => {
    if (!signupEmail || !signupPassword) {
      showMessage("Enter email & password", "error");
      return;
    }

    setSignupLoading(true);
    try {
      const result = await createUserWithEmailAndPassword(
        auth,
        signupEmail,
        signupPassword
      );

      const user = result.user;

      await sendEmailVerification(user);

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: null,
        photo: null,
        provider: "password",
        createdAt: new Date(),
      });

      await signOut(auth);

      showMessage(
        `âœ… Verification email sent to ${user.email}. Please verify before login.`
      );

      setSignupPopup(false);
      setSignupEmail("");
      setSignupPassword("");
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setSignupLoading(false);
    }
  };

  // ðŸ”¹ FORGOT PASSWORD
  const handleForgotPassword = async () => {
    if (!otpEmail) {
      showMessage("Enter email", "error");
      return;
    }

    setForgotLoading(true);
    try {
      await sendPasswordResetEmail(auth, otpEmail);
      showMessage("ðŸ“§ Password reset email sent!");
      setForgotPasswordPopup(false);
      setOtpEmail("");
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setForgotLoading(false);
    }
  };

  // ðŸ”¹ GOOGLE SIGN IN
  const googleSignIn = async () => {
    setGoogleLoading(true);
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: user.displayName,
        photo: user.photoURL,
        provider: user.providerData[0].providerId,
        createdAt: new Date(),
      });

      showMessage(`âœ… Welcome ${user.displayName}`);
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setGoogleLoading(false);
    }
  };

  return (
    <div style={{ minHeight: "100vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
      <div style={{ width: 400, padding: 30, borderRadius: 20, background: "#fff" }}>
        <h2 style={{ textAlign: "center" }}>MIST Portal</h2>

        {message && (
          <p style={{ color: message.type === "error" ? "red" : "green" }}>
            {message.text}
          </p>
        )}

        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />

        <label>
          <input type="checkbox" checked={rememberMe} onChange={(e) => setRememberMe(e.target.checked)} />
          Remember Me
        </label>

        <button onClick={login} disabled={loginLoading}>
          {loginLoading ? "Processing..." : "Login"}
        </button>

        <button onClick={googleSignIn} disabled={googleLoading}>
          {googleLoading ? "Processing..." : "Sign in with Google"}
        </button>

        <p onClick={() => setSignupPopup(true)}>Create Account</p>
        <p onClick={() => setForgotPasswordPopup(true)}>Forgot Password?</p>

        {signupPopup && (
          <>
            <input placeholder="Signup Email" value={signupEmail} onChange={(e) => setSignupEmail(e.target.value)} />
            <input type="password" placeholder="Signup Password" value={signupPassword} onChange={(e) => setSignupPassword(e.target.value)} />
            <button onClick={signup} disabled={signupLoading}>
              {signupLoading ? "Sending..." : "Signup & Verify Email"}
            </button>
          </>
        )}

        {forgotPasswordPopup && (
          <>
            <input placeholder="Email" value={otpEmail} onChange={(e) => setOtpEmail(e.target.value)} />
            <button onClick={handleForgotPassword} disabled={forgotLoading}>
              {forgotLoading ? "Sending..." : "Reset Password"}
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default AuthTest;
