// src/pages/teacherdashboard.jsx
import React, { useState } from "react";

function TeacherDashboard({ user, activeGroup, setActiveGroup }) {
  const teacherName =
    user?.displayName || user?.email?.split("@")[0] || "Teacher";

  // initial groups
  const [groups, setGroups] = useState([
    { id: "chat-7077t", name: "CSE 3A â€“ Mentoring", mentorEmail: user?.email },
    { id: "chat-7077p", name: "Project Review Group", mentorEmail: user?.email },
  ]);

  // simple fake request count + popup
  const [requestCount, setRequestCount] = useState(1); // example: one pending request
  const [showRequests, setShowRequests] = useState(false);

  const [showCreate, setShowCreate] = useState(false);
  const [groupName, setGroupName] = useState("");
  const [groupMentor, setGroupMentor] = useState(user?.email || "");
  const [groupMembers, setGroupMembers] = useState("");

  const handleCreateGroup = (e) => {
    e.preventDefault();
    if (!groupName.trim()) return;

    const newId = "chat-" + Date.now();
    const newGroup = {
      id: newId,
      name: groupName.trim(),
      mentorEmail: groupMentor.trim() || teacherName,
      memberEmails: groupMembers
        .split(",")
        .map((s) => s.trim())
        .filter(Boolean),
    };

    setGroups((prev) => [...prev, newGroup]);
    setGroupName("");
    setGroupMentor(user?.email || "");
    setGroupMembers("");
    setShowCreate(false);

    // switch to new group's chat immediately
    setActiveGroup({ id: newGroup.id, name: newGroup.name });
  };

  // handler when teacher accepts a 1-to-1 request
  const handleAcceptRequest = () => {
    // later this will come from real request object
    const studentEmail = "22131A05XX@mist.ac.in";
    const studentId = "22131A05XX";

    // create unique DM room id
    const dmId = `dm-${(user.email || "").toLowerCase()}-${studentEmail.toLowerCase()}`;

    // switch right side Chat to this 1-to-1 room
    setActiveGroup({
      id: dmId,
      name: `Chat with ${studentId}`,
    });

    setRequestCount(0);
    setShowRequests(false);
  };

  const handleDismissRequest = () => {
    setRequestCount(0);
    setShowRequests(false);
  };

  return (
    <div
      style={{
        paddingLeft: "20px",
        paddingRight: "10px",
      }}
    >
      {/* Profile + top-right notification icon */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "12px 16px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          marginBottom: "16px",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          gap: "12px",
        }}
      >
        <div>
          <h2 style={{ margin: "0 0 6px" }}>Teacher Dashboard</h2>
          <p style={{ margin: "2px 0", fontSize: "14px" }}>
            <strong>Name:</strong> {teacherName}
          </p>
          <p style={{ margin: "2px 0", fontSize: "14px" }}>
            <strong>Email:</strong> {user?.email}
          </p>
        </div>

        {/* Notifications / Requests icon */}
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <button
            onClick={() => setShowRequests(true)}
            style={{
              position: "relative",
              width: "38px",
              height: "38px",
              borderRadius: "50%",
              border: "none",
              background: "#f3f4ff",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
            }}
            title="View student requests"
          >
            <span style={{ fontSize: "18px", color: "#4b5563" }}>ðŸ””</span>

            {requestCount > 0 && (
              <span
                style={{
                  position: "absolute",
                  top: "-2px",
                  right: "-2px",
                  minWidth: "18px",
                  height: "18px",
                  padding: "0 4px",
                  borderRadius: "999px",
                  background: "#EF4444",
                  color: "white",
                  fontSize: "11px",
                  fontWeight: 700,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                {requestCount}
              </span>
            )}
          </button>
        </div>
      </div>

      {/* Requests popup */}
      {showRequests && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.3)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2000,
          }}
          onClick={() => setShowRequests(false)}
        >
          <div
            onClick={(e) => e.stopPropagation()}
            style={{
              width: "90%",
              maxWidth: "400px",
              background: "white",
              borderRadius: "12px",
              boxShadow: "0 10px 30px rgba(0,0,0,0.2)",
              padding: "16px 18px",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "10px",
              }}
            >
              <h3 style={{ margin: 0 }}>Student Requests</h3>
              <button
                onClick={() => setShowRequests(false)}
                style={{
                  border: "none",
                  background: "transparent",
                  fontSize: "18px",
                  cursor: "pointer",
                }}
              >
                Ã—
              </button>
            </div>

            {requestCount === 0 ? (
              <p style={{ margin: 0 }}>No pending requests.</p>
            ) : (
              <div
                style={{
                  borderRadius: "8px",
                  border: "1px solid #e5e7eb",
                  padding: "8px 10px",
                  marginBottom: "8px",
                  fontSize: "14px",
                }}
              >
                <strong>Student:</strong> 22131A05XX
                <br />
                <strong>Reason:</strong> Wants 1â€‘toâ€‘1 mentoring chat.
                <div style={{ marginTop: "8px", display: "flex", gap: "8px" }}>
                  <button
                    style={{
                      padding: "4px 8px",
                      borderRadius: "6px",
                      border: "none",
                      background: "#10B981",
                      color: "white",
                      fontSize: "13px",
                      cursor: "pointer",
                    }}
                    onClick={handleAcceptRequest}
                  >
                    Accept & Open Chat
                  </button>
                  <button
                    style={{
                      padding: "4px 8px",
                      borderRadius: "6px",
                      border: "1px solid #e5e7eb",
                      background: "white",
                      fontSize: "13px",
                      cursor: "pointer",
                    }}
                    onClick={handleDismissRequest}
                  >
                    Dismiss
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* My Groups */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "10px",
          }}
        >
          <h3 style={{ margin: 0 }}>My Groups</h3>
          <button
            onClick={() => setShowCreate((prev) => !prev)}
            style={{
              padding: "4px 10px",
              borderRadius: "16px",
              border: "none",
              background:
                "linear-gradient(135deg, #36D1DC 0%, #5B86E5 100%)",
              color: "white",
              cursor: "pointer",
              fontSize: "12px",
              fontWeight: 600,
            }}
          >
            {showCreate ? "Close" : "+ Create Group"}
          </button>
        </div>

        {showCreate && (
          <form
            onSubmit={handleCreateGroup}
            style={{
              display: "flex",
              flexDirection: "column",
              gap: "8px",
              marginBottom: "12px",
              padding: "10px",
              borderRadius: "8px",
              background: "#f7f7f7",
            }}
          >
            <input
              type="text"
              placeholder="Group name (e.g., CSE 3A Doubts)"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              style={{
                padding: "6px 10px",
                borderRadius: "8px",
                border: "1px solid #ddd",
                fontSize: "14px",
              }}
            />
            <input
              type="email"
              placeholder="Mentor email"
              value={groupMentor}
              onChange={(e) => setGroupMentor(e.target.value)}
              style={{
                padding: "6px 10px",
                borderRadius: "8px",
                border: "1px solid #ddd",
                fontSize: "14px",
              }}
            />
            <input
              type="text"
              placeholder="Student emails (comma separated)"
              value={groupMembers}
              onChange={(e) => setGroupMembers(e.target.value)}
              style={{
                padding: "6px 10px",
                borderRadius: "8px",
                border: "1px solid #ddd",
                fontSize: "14px",
              }}
            />
            <button
              type="submit"
              style={{
                alignSelf: "flex-start",
                padding: "6px 12px",
                borderRadius: "8px",
                border: "none",
                background:
                  "linear-gradient(135deg, #36D1DC 0%, #5B86E5 100%)",
                color: "white",
                cursor: "pointer",
                fontSize: "13px",
                fontWeight: 600,
              }}
            >
              Save Group
            </button>
          </form>
        )}

        <ul
          style={{
            listStyle: "none",
            padding: 0,
            margin: 0,
            maxHeight: "260px",
            overflowY: "auto",
          }}
        >
          {groups.map((g) => (
            <li
              key={g.id}
              onClick={() => setActiveGroup({ id: g.id, name: g.name })}
              style={{
                padding: "8px 10px",
                borderRadius: "8px",
                marginBottom: "6px",
                cursor: "pointer",
                background:
                  activeGroup?.id === g.id ? "#eef5ff" : "#f7f7f7",
                border:
                  activeGroup?.id === g.id
                    ? "1px solid #5B86E5"
                    : "1px solid transparent",
                display: "flex",
                flexDirection: "column",
                gap: "2px",
              }}
            >
              <span style={{ fontWeight: 600 }}>{g.name}</span>
              {g.mentorEmail && (
                <span style={{ fontSize: "12px", color: "#555" }}>
                  Mentor: {g.mentorEmail}
                </span>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TeacherDashboard;
