// src/pages/studentdashboard.jsx
import React, { useState } from "react";

function StudentDashboard({ user, activeGroup, setActiveGroup }) {
  const studentName =
    user?.displayName || user?.email?.split("@")[0] || "Student";

  const [groups] = useState([
    { id: "chat-7077a", name: "CSE 3A Group", mentorEmail: "mentor1@mist.ac.in" },
    { id: "chat-7077b", name: "Project Discussion", mentorEmail: "mentor2@mist.ac.in" },
  ]);

  return (
    <div
      style={{
        paddingLeft: "20px",
        paddingRight: "10px",
      }}
    >
      {/* Profile section */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          marginBottom: "16px",
        }}
      >
        <h2 style={{ margin: "0 0 8px" }}>Student Dashboard</h2>
        <p style={{ margin: "3px 0" }}>
          <strong>Name:</strong> {studentName}
        </p>
        <p style={{ margin: "3px 0" }}>
          <strong>Email:</strong> {user?.email}
        </p>
      </div>

      {/* Groups card */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        <h3 style={{ marginTop: 0, marginBottom: "10px" }}>My Groups</h3>

        <ul
          style={{
            listStyle: "none",
            padding: 0,
            margin: 0,
            maxHeight: "260px",
            overflowY: "auto",
          }}
        >
          {groups.map((g) => (
            <li
              key={g.id}
              onClick={() => setActiveGroup(g)}
              style={{
                padding: "8px 10px",
                borderRadius: "8px",
                marginBottom: "6px",
                cursor: "pointer",
                background:
                  activeGroup?.id === g.id ? "#eef5ff" : "#f7f7f7",
                border:
                  activeGroup?.id === g.id
                    ? "1px solid #5B86E5"
                    : "1px solid transparent",
                display: "flex",
                flexDirection: "column",
                gap: "2px",
              }}
            >
              <span style={{ fontWeight: 600 }}>{g.name}</span>
              <span style={{ fontSize: "12px", color: "#555" }}>
                Mentor: {g.mentorEmail}
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default StudentDashboard;
