// src/pages/studentdashboard.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function StudentDashboard({ user, activeGroup, setActiveGroup }) {
  const [data, setData] = useState(null);
  const [requestStatus, setRequestStatus] = useState("");
  const [loading, setLoading] = useState(true);

  const [groups, setGroups] = useState([]);
  const [groupName, setGroupName] = useState("");
  const [creating, setCreating] = useState(false);

  const studentName =
    user?.displayName || user?.email?.split("@")[0] || "Student";

  // Initial dashboard + status polling
  useEffect(() => {
    // Main dashboard data
    axios
      .get("http://localhost:5000/student/dashboard", {
        params: { student: studentName },
      })
      .then((res) => {
        setData(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("dashboard error:", err.message);
        setLoading(false);
      });

    // Status polling
    const interval = setInterval(() => {
      axios
        .get(
          `http://localhost:5000/student/request-status/${encodeURIComponent(
            studentName
          )}`
        )
        .then((res) => {
          if (res.data?.status) {
            setRequestStatus(res.data.status);
          }
        })
        .catch((err) => {
          console.error("status error:", err.message);
        });
    }, 3000);

    return () => clearInterval(interval);
  }, [studentName]);

  // Load groups for this student
  useEffect(() => {
    axios
      .get("http://localhost:5000/student/groups", {
        params: { student: studentName },
      })
      .then((res) => {
        // Expecting array: [{ id: "chat-7077a", name: "CSE 3A Group" }, ...]
        setGroups(res.data || []);
      })
      .catch((err) => {
        console.error("groups error:", err.message);
      });
  }, [studentName]);

  const handleCreateGroup = (e) => {
    e.preventDefault();
    if (!groupName.trim()) return;
    setCreating(true);

    axios
      .post("http://localhost:5000/student/groups/create", {
        name: groupName.trim(),
        student: studentName,
      })
      .then((res) => {
        const newGroup = res.data; // e.g. { id, name }
        setGroups((prev) => [...prev, newGroup]);
        setGroupName("");
      })
      .catch((err) => {
        console.error("create group error:", err.message);
      })
      .finally(() => setCreating(false));
  };

  if (loading) {
    return (
      <div
        style={{
          marginLeft: "20px",
          marginRight: "10px",
          padding: "15px 20px",
          background: "white",
          borderRadius: "12px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        Loading student dashboard...
      </div>
    );
  }

  return (
    <div
      style={{
        paddingLeft: "20px",
        paddingRight: "10px",
        marginBottom: "30px",
      }}
    >
      <h2 style={{ margin: "0 0 15px" }}>Student Dashboard</h2>

      {/* Profile + Status cards */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "15px",
          marginBottom: "20px",
        }}
      >
        {/* Profile */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "16px 18px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "8px" }}>Profile</h3>
          <p style={{ margin: "3px 0" }}>
            <strong>Name:</strong> {studentName}
          </p>
          <p style={{ margin: "3px 0" }}>
            <strong>Email:</strong> {user?.email}
          </p>
        </div>

        {/* Room request status */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "16px 18px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "8px" }}>
            Room Request Status
          </h3>
          <p style={{ margin: 0 }}>
            Current status:{" "}
            <span
              style={{
                fontWeight: 600,
                color:
                  requestStatus === "Approved"
                    ? "green"
                    : requestStatus === "Rejected"
                    ? "red"
                    : "#555",
              }}
            >
              {requestStatus || "No request yet"}
            </span>
          </p>
        </div>

        {/* Today summary */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "16px 18px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "8px" }}>
            Today&apos;s Summary
          </h3>
          {data ? (
            <>
              <p style={{ margin: "3px 0" }}>
                <strong>Classes today:</strong>{" "}
                {data.classesCount ?? "-"}
              </p>
              <p style={{ margin: "3px 0" }}>
                <strong>Pending requests:</strong>{" "}
                {data.pendingRequests ?? "-"}
              </p>
            </>
          ) : (
            <p style={{ margin: 0 }}>No dashboard data available.</p>
          )}
        </div>
      </div>

      {/* Groups card */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        <h3 style={{ marginTop: 0, marginBottom: "10px" }}>My Groups</h3>

        {/* Create group form */}
        <form
          onSubmit={handleCreateGroup}
          style={{
            display: "flex",
            gap: "8px",
            marginBottom: "10px",
          }}
        >
          <input
            type="text"
            placeholder="New group name"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
            style={{
              flex: 1,
              padding: "6px 10px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              fontSize: "14px",
            }}
          />
          <button
            type="submit"
            disabled={creating}
            style={{
              padding: "6px 12px",
              borderRadius: "8px",
              border: "none",
              background:
                "linear-gradient(135deg, #36D1DC 0%, #5B86E5 100%)",
              color: "white",
              cursor: "pointer",
              fontSize: "13px",
              fontWeight: 600,
              opacity: creating ? 0.7 : 1,
            }}
          >
            {creating ? "Creating..." : "+ Create"}
          </button>
        </form>

        {/* Groups list */}
        {groups.length === 0 ? (
          <p style={{ margin: 0 }}>You are not in any groups yet.</p>
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              margin: 0,
              maxHeight: "200px",
              overflowY: "auto",
            }}
          >
            {groups.map((g) => (
              <li
                key={g.id}
                onClick={() => setActiveGroup(g)}
                style={{
                  padding: "8px 10px",
                  borderRadius: "8px",
                  marginBottom: "6px",
                  cursor: "pointer",
                  background:
                    activeGroup?.id === g.id ? "#eef5ff" : "#f7f7f7",
                  border:
                    activeGroup?.id === g.id
                      ? "1px solid #5B86E5"
                      : "1px solid transparent",
                }}
              >
                {g.name}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default StudentDashboard;
