// src/pages/studentdashboard.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function StudentDashboard({ user, activeGroup, setActiveGroup }) {
  const [groups, setGroups] = useState([]);
  const [groupName, setGroupName] = useState("");
  const [groupMentor, setGroupMentor] = useState("");
  const [groupMembers, setGroupMembers] = useState(""); // comma-separated emails
  const [creating, setCreating] = useState(false);
  const [loadingGroups, setLoadingGroups] = useState(true);

  const studentName =
    user?.displayName || user?.email?.split("@")[0] || "Student";

  // Load groups for this student
  useEffect(() => {
    setLoadingGroups(true);
    axios
      .get("http://localhost:5000/student/groups", {
        params: { student: studentName },
      })
      .then((res) => {
        // expected: [{ id: "chat-7077a", name: "CSE 3A Group" }, ...]
        setGroups(res.data || []);
        setLoadingGroups(false);
      })
      .catch((err) => {
        console.error("groups error:", err.message);
        setLoadingGroups(false);
      });
  }, [studentName]);

  const handleCreateGroup = (e) => {
    e.preventDefault();
    if (!groupName.trim()) return;

    setCreating(true);

    axios
      .post("http://localhost:5000/student/groups/create", {
        name: groupName.trim(),
        mentorEmail: groupMentor.trim() || null,
        memberEmails: groupMembers
          .split(",")
          .map((s) => s.trim())
          .filter(Boolean),
        createdBy: user.email,
      })
      .then((res) => {
        const newGroup = res.data; // { id, name, ... }
        setGroups((prev) => [...prev, newGroup]);
        setGroupName("");
        setGroupMentor("");
        setGroupMembers("");
      })
      .catch((err) => {
        console.error("create group error:", err.message);
      })
      .finally(() => setCreating(false));
  };

  return (
    <div
      style={{
        paddingLeft: "20px",
        paddingRight: "10px",
      }}
    >
      {/* Profile section */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          marginBottom: "16px",
        }}
      >
        <h2 style={{ margin: "0 0 8px" }}>Student Dashboard</h2>
        <p style={{ margin: "3px 0" }}>
          <strong>Name:</strong> {studentName}
        </p>
        <p style={{ margin: "3px 0" }}>
          <strong>Email:</strong> {user?.email}
        </p>
      </div>

      {/* Groups card */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "16px 18px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        <h3 style={{ marginTop: 0, marginBottom: "10px" }}>My Groups</h3>

        {/* Create group form */}
        <form
          onSubmit={handleCreateGroup}
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "8px",
            marginBottom: "12px",
          }}
        >
          <input
            type="text"
            placeholder="Group name (e.g., CSE 3A Project Group)"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
            style={{
              padding: "6px 10px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              fontSize: "14px",
            }}
          />
          <input
            type="email"
            placeholder="Mentor email (optional)"
            value={groupMentor}
            onChange={(e) => setGroupMentor(e.target.value)}
            style={{
              padding: "6px 10px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              fontSize: "14px",
            }}
          />
          <input
            type="text"
            placeholder="Student emails (comma separated)"
            value={groupMembers}
            onChange={(e) => setGroupMembers(e.target.value)}
            style={{
              padding: "6px 10px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              fontSize: "14px",
            }}
          />

          <button
            type="submit"
            disabled={creating}
            style={{
              alignSelf: "flex-start",
              padding: "6px 12px",
              borderRadius: "8px",
              border: "none",
              background:
                "linear-gradient(135deg, #36D1DC 0%, #5B86E5 100%)",
              color: "white",
              cursor: "pointer",
              fontSize: "13px",
              fontWeight: 600,
              opacity: creating ? 0.7 : 1,
            }}
          >
            {creating ? "Creating..." : "+ Create Group"}
          </button>
        </form>

        {/* Groups list */}
        {loadingGroups ? (
          <p style={{ margin: 0 }}>Loading groups...</p>
        ) : groups.length === 0 ? (
          <p style={{ margin: 0 }}>You are not in any groups yet.</p>
        ) : (
          <ul
            style={{
              listStyle: "none",
              padding: 0,
              margin: 0,
              maxHeight: "260px",
              overflowY: "auto",
            }}
          >
            {groups.map((g) => (
              <li
                key={g.id}
                onClick={() => setActiveGroup(g)}
                style={{
                  padding: "8px 10px",
                  borderRadius: "8px",
                  marginBottom: "6px",
                  cursor: "pointer",
                  background:
                    activeGroup?.id === g.id ? "#eef5ff" : "#f7f7f7",
                  border:
                    activeGroup?.id === g.id
                      ? "1px solid #5B86E5"
                      : "1px solid transparent",
                  display: "flex",
                  flexDirection: "column",
                  gap: "2px",
                }}
              >
                <span style={{ fontWeight: 600 }}>{g.name}</span>
                {g.mentorEmail && (
                  <span style={{ fontSize: "12px", color: "#555" }}>
                    Mentor: {g.mentorEmail}
                  </span>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default StudentDashboard;
