import { useState, useEffect } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signInWithPopup,
  sendPasswordResetEmail,
  signOut,
  sendEmailVerification,
  setPersistence,
  browserLocalPersistence,
  browserSessionPersistence
} from "firebase/auth";
import { auth, googleProvider, db } from "./firebase";
import { doc, setDoc } from "firebase/firestore";

function MISTAuth() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [signupPopup, setSignupPopup] = useState(false);
  const [forgotPopup, setForgotPopup] = useState(false);
  const [signupEmail, setSignupEmail] = useState("");
  const [signupPassword, setSignupPassword] = useState("");
  const [otpSent, setOtpSent] = useState(false);

  // Helper to show messages
  const showMessage = (msg, type = "success") => alert(msg);

  // ðŸ”¹ Login
  const login = async () => {
    setLoading(true);
    try {
      await setPersistence(auth, rememberMe ? browserLocalPersistence : browserSessionPersistence);
      const result = await signInWithEmailAndPassword(auth, email, password);
      if (!result.user.emailVerified) {
        showMessage("ðŸ” Please verify your email first!", "error");
        await auth.signOut();
        return;
      }
      showMessage(`âœ… Welcome back, ${result.user.email}`);
    } catch (e) {
      showMessage(e.message, "error");
    }
    setLoading(false);
  };

  // ðŸ”¹ Signup
  const signup = async () => {
    if (!signupEmail || !signupPassword) return showMessage("Enter email and password!");
    setLoading(true);
    try {
      const result = await createUserWithEmailAndPassword(auth, signupEmail, signupPassword);
      const user = result.user;

      // Save to Firestore
      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: null,
        photo: null,
        provider: "password",
        createdAt: new Date()
      });

      // Send email verification
      await sendEmailVerification(user);
      setOtpSent(true);
      showMessage("âœ… OTP sent to your email. Please verify!");
    } catch (e) {
      showMessage(e.message, "error");
    }
    setLoading(false);
  };

  // ðŸ”¹ Google Sign-In
  const googleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: user.displayName,
        photo: user.photoURL,
        provider: user.providerData[0].providerId,
        createdAt: new Date()
      });

      showMessage(`âœ… Google Sign-In success: ${user.email}`);
    } catch (e) {
      showMessage(e.message, "error");
    }
  };

  // ðŸ”¹ Forgot Password
  const forgotPassword = async () => {
    if (!email) return showMessage("Enter your email!");
    try {
      await sendPasswordResetEmail(auth, email);
      showMessage("âœ… Password reset email sent!");
      setForgotPopup(false);
    } catch (e) {
      showMessage(e.message, "error");
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "linear-gradient(135deg, #667eea, #764ba2)",
      fontFamily: "Arial, sans-serif"
    }}>
      {/* ðŸ”¹ Login Card */}
      <div style={{
        background: "#fff",
        padding: "40px",
        borderRadius: "12px",
        boxShadow: "0 10px 25px rgba(0,0,0,0.2)",
        width: "350px",
        textAlign: "center"
      }}>
        <h1 style={{ color: "#764ba2", marginBottom: "20px" }}>MIST Login</h1>

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: "80%", padding: "10px", margin: "10px 0", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "80%", padding: "10px", margin: "10px 0", borderRadius: "6px", border: "1px solid #ccc" }}
        />

        <div style={{ textAlign: "left", width: "80%", margin: "10px auto" }}>
          <input type="checkbox" checked={rememberMe} onChange={(e) => setRememberMe(e.target.checked)} /> Remember Me
          <span
            style={{ float: "right", color: "#764ba2", cursor: "pointer" }}
            onClick={() => setForgotPopup(true)}
          >
            Forgot Password?
          </span>
        </div>

        <button
          onClick={login}
          style={{
            width: "80%",
            padding: "12px",
            background: "#764ba2",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            margin: "10px 0",
            cursor: "pointer",
            fontWeight: "bold"
          }}
        >
          {loading ? "Loading..." : "Sign In"}
        </button>

        <p>---------------- or ----------------</p>

        <button
          onClick={googleSignIn}
          style={{
            width: "80%",
            padding: "12px",
            background: "#4285F4",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            margin: "10px 0",
            cursor: "pointer",
            fontWeight: "bold"
          }}
        >
          Sign in with Google
        </button>

        <p style={{ marginTop: "20px" }}>
          Don't have an account?{" "}
          <span
            style={{ color: "#764ba2", cursor: "pointer", fontWeight: "bold" }}
            onClick={() => setSignupPopup(true)}
          >
            Sign Up
          </span>
        </p>
      </div>

      {/* ðŸ”¹ Signup Popup */}
      {signupPopup && (
        <div style={{
          position: "fixed",
          top: 0, left: 0,
          width: "100%", height: "100%",
          background: "rgba(0,0,0,0.5)",
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}>
          <div style={{
            background: "#fff",
            padding: "30px",
            borderRadius: "12px",
            width: "300px",
            textAlign: "center"
          }}>
            <h2 style={{ color: "#764ba2" }}>MIST Sign Up</h2>
            <input
              type="email"
              placeholder="Email"
              value={signupEmail}
              onChange={(e) => setSignupEmail(e.target.value)}
              style={{ width: "80%", padding: "10px", margin: "10px 0", borderRadius: "6px", border: "1px solid #ccc" }}
            />
            <input
              type="password"
              placeholder="Password"
              value={signupPassword}
              onChange={(e) => setSignupPassword(e.target.value)}
              style={{ width: "80%", padding: "10px", margin: "10px 0", borderRadius: "6px", border: "1px solid #ccc" }}
            />

            <button
              onClick={signup}
              style={{
                width: "80%",
                padding: "12px",
                background: "#764ba2",
                color: "#fff",
                border: "none",
                borderRadius: "6px",
                margin: "10px 0",
                cursor: "pointer",
                fontWeight: "bold"
              }}
            >
              {loading ? "Loading..." : otpSent ? "OTP Sent!" : "Sign Up"}
            </button>

            <p
              style={{ color: "#764ba2", cursor: "pointer", marginTop: "10px" }}
              onClick={() => setSignupPopup(false)}
            >
              Cancel
            </p>
          </div>
        </div>
      )}

      {/* ðŸ”¹ Forgot Password Popup */}
      {forgotPopup && (
        <div style={{
          position: "fixed",
          top: 0, left: 0,
          width: "100%", height: "100%",
          background: "rgba(0,0,0,0.5)",
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}>
          <div style={{
            background: "#fff",
            padding: "30px",
            borderRadius: "12px",
            width: "300px",
            textAlign: "center"
          }}>
            <h2 style={{ color: "#764ba2" }}>Reset Password</h2>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ width: "80%", padding: "10px", margin: "10px 0", borderRadius: "6px", border: "1px solid #ccc" }}
            />
            <button
              onClick={forgotPassword}
              style={{
                width: "80%",
                padding: "12px",
                background: "#764ba2",
                color: "#fff",
                border: "none",
                borderRadius: "6px",
                margin: "10px 0",
                cursor: "pointer",
                fontWeight: "bold"
              }}
            >
              Send OTP / Reset
            </button>

            <p
              style={{ color: "#764ba2", cursor: "pointer", marginTop: "10px" }}
              onClick={() => setForgotPopup(false)}
            >
              Cancel
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default MISTAuth;
