import { useEffect, useState } from "react";
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp
} from "firebase/firestore";
import { auth, db } from "./firebase";

function Chat() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  // üîê logged-in user
  const user = auth.currentUser;

  // üö´ safety check
  if (!user) return <h3>Please login</h3>;

  const chatId = "student1_mentor1"; // temporary (we'll improve later)

  // üîÅ Real-time listener
  useEffect(() => {
    const q = query(
      collection(db, "chats", chatId, "messages"),
      orderBy("timestamp")
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      setMessages(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data()
        }))
      );
    });

    return () => unsubscribe();
  }, []);

  // üì§ Send message
  const sendMessage = async () => {
    if (text.trim() === "") return;

    await addDoc(
      collection(db, "chats", chatId, "messages"),
      {
        senderId: user.uid,
        message: text,
        timestamp: serverTimestamp()
      }
    );

    setText("");
  };

  return (
    <div style={{ width: "300px", margin: "auto" }}>
      <h2>Secure Chat</h2>

      <div style={{ height: "200px", overflowY: "auto", border: "1px solid #ccc" }}>
        {messages.map((m) => (
          <p key={m.id}>
            <b>{m.senderId === user.uid ? "Me" : "Other"}:</b> {m.message}
          </p>
        ))}
      </div>

      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Type message"
      />
      <button onClick={sendMessage}>Send</button>
      <button onClick={() => auth.signOut()}>Logout</button>
    </div>
  );
}

export default Chat;
