// App.jsx
import { useState, useEffect } from "react";
import AuthTest from "./AuthTest";
import { auth } from "./firebase";
import StudentDashboard from "./pages/studentdashboard";

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showAuth, setShowAuth] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      setLoading(false);
      if (currentUser) {
        setShowAuth(false);
      }
    });
    return () => unsubscribe();
  }, []);

  if (loading) {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "100vh",
          background:
            "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)",
          fontSize: "18px",
          color: "#666"
        }}
      >
        Loading MIST Portal...
      </div>
    );
  }

  if (user && !showAuth) {
    return (
      <StudentDashboard
        user={user}
        onLogout={() => {
          auth.signOut();
          setShowAuth(true);
        }}
      />
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background:
          "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)"
      }}
    >
      <AuthTest />
    </div>
  );
}

export default App;
