// src/pages/studentdashboard.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function StudentDashboard({ user }) {
  const [data, setData] = useState(null);
  const [requestStatus, setRequestStatus] = useState("");
  const [loading, setLoading] = useState(true);

  // Derive student name from Firebase user
  const studentName =
    user?.displayName || user?.email?.split("@")[0] || "Student One";

  useEffect(() => {
    // 1) Load main dashboard data
    axios
      .get("http://localhost:5000/student/dashboard", {
        params: { student: studentName },
      })
      .then((res) => {
        console.log("✅ dashboard data:", res.data);
        setData(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("❌ dashboard error:", err.message);
        setLoading(false);
      });

    // 2) Poll room‑request status every 3 seconds
    const interval = setInterval(() => {
      axios
        .get(
          `http://localhost:5000/student/request-status/${encodeURIComponent(
            studentName
          )}`
        )
        .then((res) => {
          if (res.data?.status) {
            setRequestStatus(res.data.status);
          }
        })
        .catch((err) => {
          console.error("status error:", err.message);
        });
    }, 3000);

    return () => clearInterval(interval);
  }, [studentName]);

  if (loading) {
    return (
      <div
        style={{
          margin: "0 20px 20px",
          padding: "15px 20px",
          background: "white",
          borderRadius: "12px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
        }}
      >
        Loading student dashboard...
      </div>
    );
  }

  return (
    <div
      style={{
        padding: "0 20px",
        marginBottom: "30px",
      }}
    >
      {/* Title */}
      <h2 style={{ margin: "0 0 15px" }}>Student Dashboard</h2>

      {/* Cards grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(260px, 1fr))",
          gap: "20px",
          marginBottom: "20px",
        }}
      >
        {/* Profile card */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "20px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "10px" }}>Profile</h3>
          <p style={{ margin: "4px 0" }}>
            <strong>Name:</strong> {studentName}
          </p>
          <p style={{ margin: "4px 0" }}>
            <strong>Email:</strong> {user?.email}
          </p>
        </div>

        {/* Request status card */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "20px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "10px" }}>
            Room Request Status
          </h3>
          <p style={{ margin: 0 }}>
            Current status:{" "}
            <span
              style={{
                fontWeight: 600,
                color:
                  requestStatus === "Approved"
                    ? "green"
                    : requestStatus === "Rejected"
                    ? "red"
                    : "#555",
              }}
            >
              {requestStatus || "No request yet"}
            </span>
          </p>
        </div>

        {/* Today summary using backend data (change keys as per your API) */}
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "20px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
          }}
        >
          <h3 style={{ marginTop: 0, marginBottom: "10px" }}>
            Today&apos;s Summary
          </h3>
          {data ? (
            <>
              <p style={{ margin: "4px 0" }}>
                <strong>Classes today:</strong>{" "}
                {data.classesCount ?? "-"}
              </p>
              <p style={{ margin: "4px 0" }}>
                <strong>Pending requests:</strong>{" "}
                {data.pendingRequests ?? "-"}
              </p>
            </>
          ) : (
            <p style={{ margin: 0 }}>No dashboard data available.</p>
          )}
        </div>
      </div>

      {/* Optional debug block */}
      <div
        style={{
          background: "white",
          borderRadius: "12px",
          padding: "15px 20px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
        }}
      >
        <h4 style={{ marginTop: 0 }}>Debug data (backend JSON)</h4>
        <pre
          style={{
            margin: 0,
            maxHeight: "200px",
            overflow: "auto",
            fontSize: "12px",
            background: "#f7f7f7",
            padding: "10px",
            borderRadius: "8px",
          }}
        >
{JSON.stringify(data, null, 2)}
        </pre>
      </div>
    </div>
  );
}

export default StudentDashboard;
