import { useEffect, useState } from "react";
import { signInWithPopup, signOut } from "firebase/auth";
import { auth, googleProvider } from "./firebase";
import Chat from "./Chat";

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
  }, []);

  const login = () => signInWithPopup(auth, googleProvider);
  const logout = () => signOut(auth);

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "30px", background: "#f0f2f5", minHeight: "100vh" }}>
      <h1 style={{ textAlign: "center", color: "#8B5CF6" }}>ðŸš€ MIST CHAT TEST</h1>
      
      {user ? (
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <img src={user.photoURL} style={{ width: "50px", borderRadius: "50%", marginRight: "10px" }} />
          <span>{user.displayName} ({user.email})</span>
          <button onClick={logout} style={{ marginLeft: "20px", padding: "10px 20px", background: "#ef4444", color: "white", border: "none", borderRadius: "20px" }}>
            Logout
          </button>
        </div>
      ) : (
        <div style={{ textAlign: "center" }}>
          <button onClick={login} style={{ padding: "15px 30px", background: "#8B5CF6", color: "white", border: "none", borderRadius: "25px", fontSize: "18px", cursor: "pointer" }}>
            Login with Google
          </button>
        </div>
      )}

      {user && (
        <div style={{ display: "flex", gap: "20px", justifyContent: "center", flexWrap: "wrap" }}>
          <div style={{ background: "#e1f5fe", padding: "20px", borderRadius: "20px" }}>
            <h3>ðŸ‘© Alice</h3>
            <Chat chatId="chat-7077a" userId={user.uid} userName={`${user.displayName} ðŸ’œ`} />
          </div>
          <div style={{ background: "#f3e5f5", padding: "20px", borderRadius: "20px" }}>
            <h3>ðŸ‘¨ Bob</h3>
            <Chat chatId="chat-7077a" userId={user.uid + "_bob"} userName={`${user.displayName} ðŸ”µ`} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
