import { useState } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  signInWithPopup
} from "firebase/auth";
import { auth, googleProvider, db } from "./firebase";
import { doc, setDoc } from "firebase/firestore";

function AuthTest() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);

  const showMessage = (text, type = "success") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  const signup = async () => {
    setLoading(true);
    try {
      const result = await createUserWithEmailAndPassword(auth, email, password);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: null,
        photo: null,
        provider: "password",
        createdAt: new Date()
      });

      showMessage("ðŸŽ‰ Account created successfully!");
      setEmail("");
      setPassword("");
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setLoading(false);
    }
  };

  const login = async () => {
    setLoading(true);
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      const user = result.user;

      showMessage(`âœ… Welcome back, ${user.email}`);
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setLoading(false);
    }
  };

  const googleSignIn = async () => {
    setLoading(true);
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: user.displayName,
        photo: user.photoURL,
        provider: user.providerData[0].providerId,
        createdAt: new Date()
      });

      showMessage(`âœ… Welcome ${user.displayName}`);
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      showMessage("Logged out successfully âœ…");
    } catch (e) {
      showMessage(e.message, "error");
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
      }}
    >
      <div
        style={{
          width: "380px",
          padding: "35px 30px",
          borderRadius: "20px",
          backgroundColor: "white",
          boxShadow: "0 15px 40px rgba(0,0,0,0.2)",
          textAlign: "center",
          position: "relative"
        }}
      >
        <h1 style={{ color: "#FF4E50", marginBottom: "25px" }}>MIST Portal</h1>

        {message && (
          <div
            style={{
              position: "absolute",
              top: "-60px",
              left: "50%",
              transform: "translateX(-50%)",
              padding: "12px 20px",
              borderRadius: "12px",
              fontWeight: "bold",
              backgroundColor: message.type === "success" ? "#D4EDDA" : "#F8D7DA",
              color: message.type === "success" ? "#155724" : "#721C24",
              border: message.type === "success" ? "1px solid #C3E6CB" : "1px solid #F5C6CB",
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)"
            }}
          >
            {message.text}
          </div>
        )}

        <div style={{ marginBottom: "20px", textAlign: "left" }}>
          <label
            style={{
              fontSize: "14px",
              fontWeight: "600",
              color: "#333",
              display: "block",
              marginBottom: "5px"
            }}
          >
            Email
          </label>
          <input
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{
              width: "100%",
              padding: "12px",
              borderRadius: "12px",
              border: "1px solid #ccc",
              outline: "none",
              fontSize: "14px",
              transition: "0.3s",
            }}
          />
        </div>

        <div style={{ marginBottom: "25px", textAlign: "left" }}>
          <label
            style={{
              fontSize: "14px",
              fontWeight: "600",
              color: "#333",
              display: "block",
              marginBottom: "5px"
            }}
          >
            Password
          </label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{
              width: "100%",
              padding: "12px",
              borderRadius: "12px",
              border: "1px solid #ccc",
              outline: "none",
              fontSize: "14px",
            }}
          />
        </div>

        <button
          onClick={signup}
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            marginBottom: "12px",
            fontWeight: "bold",
            fontSize: "16px",
            background: "linear-gradient(90deg, #FF4E50 0%, #F9D423 100%)",
            color: "white",
            cursor: "pointer",
            transition: "0.3s"
          }}
        >
          {loading ? "Processing..." : "Signup"}
        </button>

        <button
          onClick={login}
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            marginBottom: "12px",
            fontWeight: "bold",
            fontSize: "16px",
            background: "linear-gradient(90deg, #6A11CB 0%, #2575FC 100%)",
            color: "white",
            cursor: "pointer",
            transition: "0.3s"
          }}
        >
          {loading ? "Processing..." : "Login"}
        </button>

        <button
          onClick={googleSignIn}
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            marginBottom: "12px",
            fontWeight: "bold",
            fontSize: "16px",
            backgroundColor: "#DB4437",
            color: "white",
            cursor: "pointer",
            transition: "0.3s"
          }}
        >
          {loading ? "Processing..." : "Sign in with Google"}
        </button>

        <button
          onClick={logout}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            fontWeight: "bold",
            fontSize: "16px",
            backgroundColor: "#555",
            color: "white",
            cursor: "pointer",
            transition: "0.3s"
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default AuthTest;
