import { useState } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  sendEmailVerification,
  sendPasswordResetEmail,
  signInWithPopup,
  signOut
} from "firebase/auth";
import { auth, googleProvider, db } from "./firebase";
import { doc, setDoc } from "firebase/firestore";

function AuthTest() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(null);

  // üîπ FIX: separate loading states
  const [loginLoading, setLoginLoading] = useState(false);
  const [googleLoading, setGoogleLoading] = useState(false);
  const [signupLoading, setSignupLoading] = useState(false);
  const [forgotLoading, setForgotLoading] = useState(false);

  const [rememberMe, setRememberMe] = useState(false);
  const [forgotPasswordPopup, setForgotPasswordPopup] = useState(false);
  const [signupPopup, setSignupPopup] = useState(false);

  const [otpEmail, setOtpEmail] = useState("");
  const [signupEmail, setSignupEmail] = useState("");
  const [signupPassword, setSignupPassword] = useState("");

  const showMessage = (text, type = "success") => {
    setMessage({ text, type });
    setTimeout(() => setMessage(null), 4000);
  };

  // üîπ Login
  const login = async () => {
    setLoginLoading(true);
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      const user = result.user;

      if (!user.emailVerified) {
        showMessage("üîê Verify your email first!", "error");
        await signOut(auth);
        setLoginLoading(false); // ‚úÖ FIX
        return;
      }

      if (rememberMe) localStorage.setItem("rememberedUser", user.uid);

      showMessage(`‚úÖ Welcome back, ${user.email}`);
    } catch (e) {
      showMessage(e.message, "error");
    } finally {
      setLoginLoading(false);
    }
  };

  // üîπ Signup with email verification
  const signup = async () => {
    if (!signupEmail || !signupPassword) {
      showMessage("Enter email and password", "error");
      return;
    }

    setSignupLoading(true);
    try {
      const result = await createUserWithEmailAndPassword(
        auth,
        signupEmail,
        signupPassword
      );

      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: null,
        photo: null,
        provider: "password",
        createdAt: new Date()
      });

      await sendEmailVerification(user);

      // ‚úÖ FIX: logout user until verified
      await signOut(auth);

      showMessage(
        `üéâ Signup successful! Verification email sent to ${user.email}.`
      );

      setSignupPopup(false);
      setSignupEmail("");
      setSignupPassword("");
    } catch (e) {
      showMessage(`‚ö†Ô∏è ${e.message}`, "error");
    } finally {
      setSignupLoading(false);
    }
  };

  // üîπ Forgot password
  const handleForgotPassword = async () => {
    if (!otpEmail) {
      showMessage("Enter email for password reset", "error");
      return;
    }

    setForgotLoading(true);
    try {
      await sendPasswordResetEmail(auth, otpEmail);
      showMessage("üìß Password reset email sent! Check your inbox.");
      setForgotPasswordPopup(false);
      setOtpEmail("");
    } catch (e) {
      showMessage(`‚ö†Ô∏è ${e.message}`, "error");
    } finally {
      setForgotLoading(false);
    }
  };

  // üîπ Google Sign-In
  const googleSignIn = async () => {
    setGoogleLoading(true);
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: user.displayName,
        photo: user.photoURL,
        provider: user.providerData[0].providerId,
        createdAt: new Date()
      });

      showMessage(`‚úÖ Welcome ${user.displayName}`);
    } catch (e) {
      showMessage(`‚ö†Ô∏è ${e.message}`, "error");
    } finally {
      setGoogleLoading(false);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          width: "400px",
          padding: "40px 30px",
          borderRadius: "20px",
          backgroundColor: "white",
          boxShadow: "0 15px 40px rgba(0,0,0,0.2)",
          textAlign: "center",
          position: "relative",
        }}
      >
        <h1 style={{ color: "#FF4E50", marginBottom: "25px" }}>MIST Portal</h1>

        {message && (
          <div
            style={{
              position: "absolute",
              top: "-60px",
              left: "50%",
              transform: "translateX(-50%)",
              padding: "12px 20px",
              borderRadius: "12px",
              fontWeight: "bold",
              backgroundColor:
                message.type === "success" ? "#D4EDDA" : "#F8D7DA",
              color: message.type === "success" ? "#155724" : "#721C24",
              border:
                message.type === "success"
                  ? "1px solid #C3E6CB"
                  : "1px solid #F5C6CB",
            }}
          >
            {message.text}
          </div>
        )}

        {/* üîπ Login Form */}
        <input
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: "95%", padding: "12px", borderRadius: "12px" }}
        />

        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "95%", padding: "12px", borderRadius: "12px", marginTop: "10px" }}
        />

        <button
          onClick={login}
          disabled={loginLoading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            marginTop: "15px",
            background: "linear-gradient(90deg, #6A11CB 0%, #2575FC 100%)",
            color: "white",
            fontWeight: "bold",
          }}
        >
          {loginLoading ? "Processing..." : "Login"}
        </button>

        <button
          onClick={googleSignIn}
          disabled={googleLoading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            marginTop: "10px",
            backgroundColor: "#DB4437",
            color: "white",
            fontWeight: "bold",
          }}
        >
          {googleLoading ? "Processing..." : "Sign in with Google"}
        </button>
      </div>
    </div>
  );
}

export default AuthTest;
