import { useState } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  signInWithPopup
} from "firebase/auth";
import { auth, googleProvider } from "./firebase";
import { doc, setDoc } from "firebase/firestore";
import { db } from "./firebase";

function AuthTest() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // ðŸ”¹ Email/Password Signup
  const signup = async () => {
    try {
      const result = await createUserWithEmailAndPassword(auth, email, password);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: null,
        photo: null,
        provider: "password",
        createdAt: new Date()
      });

      console.log("Signup success:", user.uid);
    } catch (e) {
      console.error(e.message);
    }
  };

  // ðŸ”¹ Email/Password Login
  const login = async () => {
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      const user = result.user;

      console.log("Login success:", user.uid);
    } catch (e) {
      console.error(e.message);
    }
  };

  // ðŸ”¹ Google Sign-In
  const googleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        email: user.email,
        name: user.displayName,
        photo: user.photoURL,
        provider: user.providerData[0].providerId,
        createdAt: new Date()
      });

      console.log("Google Sign-In success:", user.uid, user.email);
    } catch (e) {
      console.error(e.message);
    }
  };

  // ðŸ”¹ Logout
  const logout = async () => {
    try {
      await signOut(auth);
      console.log("Logged out");
    } catch (e) {
      console.error(e.message);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        background: "linear-gradient(135deg, #6B73FF 0%, #000DFF 100%)",
        fontFamily: "Arial, sans-serif",
        color: "#333",
      }}
    >
      <div
        style={{
          background: "#fff",
          padding: "40px 30px",
          borderRadius: "15px",
          boxShadow: "0px 10px 30px rgba(0,0,0,0.1)",
          width: "350px",
          textAlign: "center",
        }}
      >
        <h2 style={{ marginBottom: "30px", color: "#333" }}>Firebase Auth</h2>

        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{
            width: "100%",
            padding: "12px",
            marginBottom: "15px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            fontSize: "14px",
          }}
        />

        <input
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{
            width: "100%",
            padding: "12px",
            marginBottom: "20px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            fontSize: "14px",
          }}
        />

        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
          <button
            onClick={signup}
            style={{
              padding: "10px 20px",
              border: "none",
              borderRadius: "8px",
              backgroundColor: "#6B73FF",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#5A63E5")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#6B73FF")}
          >
            Signup
          </button>

          <button
            onClick={login}
            style={{
              padding: "10px 20px",
              border: "none",
              borderRadius: "8px",
              backgroundColor: "#00CFFF",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#00B2E6")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#00CFFF")}
          >
            Login
          </button>
        </div>

        <button
          onClick={googleSignIn}
          style={{
            width: "100%",
            padding: "12px",
            border: "none",
            borderRadius: "8px",
            backgroundColor: "#DB4437",
            color: "#fff",
            fontWeight: "bold",
            cursor: "pointer",
            marginBottom: "15px",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#C33D30")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#DB4437")}
        >
          Sign in with Google
        </button>

        <button
          onClick={logout}
          style={{
            width: "100%",
            padding: "12px",
            border: "none",
            borderRadius: "8px",
            backgroundColor: "#555",
            color: "#fff",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#444")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#555")}
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default AuthTest;
