import { useEffect, useState, useRef, useCallback } from "react";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp, doc, getDoc } from "firebase/firestore";
import { db } from "./firebase";

function Chat({ chatId = "global-chat", userId, userName = "User" }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [allUserChats, setAllUserChats] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ðŸ”¥ USER-LINKED CHATS - Loads YOUR personal chat history FIRST
  useEffect(() => {
    if (!userId) return;

    // 1. Load user's personal chat history
    const loadUserChats = async () => {
      try {
        const userDoc = await getDoc(doc(db, "users", userId));
        if (userDoc.exists()) {
          const userData = userDoc.data();
          console.log("ðŸ‘¤ User chats found:", userData.chats);
          setAllUserChats(userData.chats || []);
        }
      } catch (error) {
        console.log("No user chats yet - will create");
      }
    };

    loadUserChats();
  }, [userId]);

  // 2. Load ALL messages from global chat (everyone sees)
  useEffect(() => {
    console.log("ðŸ”— Loading messages for chatId:", chatId, "userId:", userId);
    
    const q = query(
      collection(db, "chats", chatId, "messages"), 
      orderBy("timestamp")
    );
    
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const loadedMessages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        timestamp: doc.data().timestamp?.toDate() || new Date()
      }));
      
      console.log("ðŸ’¾ LOADED", loadedMessages.length, "messages for", chatId);
      setMessages(loadedMessages);
    });
    
    return unsubscribe;
  }, [chatId]);

  // ðŸ”¥ SAVE MESSAGE + LINK TO USER
  const sendMessage = useCallback(async () => {
    const trimmedText = text.trim();
    if (!trimmedText || !userId) return;

    // Instant UI
    const optimisticId = `temp-${Date.now()}`;
    setMessages(prev => [{
      id: optimisticId,
      senderId: userId,
      senderName: userName,
      message: trimmedText,
      timestamp: new Date()
    }, ...prev]);
    
    setText("");

    try {
      // 1. Save to GLOBAL chat
      await addDoc(collection(db, "chats", chatId, "messages"), {
        senderId: userId,
        senderName: userName,
        message: trimmedText,
        timestamp: serverTimestamp()
      });

      // 2. LINK to USER'S personal history
      await setDoc(doc(db, "users", userId), {
        chats: arrayUnion({
          chatId: chatId,
          message: trimmedText,
          timestamp: serverTimestamp(),
          lastActive: serverTimestamp()
        })
      }, { merge: true });

      console.log("âœ… SAVED to global + user profile");
    } catch (error) {
      console.error("âŒ Save failed:", error);
      setMessages(prev => prev.filter(m => m.id !== optimisticId));
      setText(trimmedText);
    }
  }, [text, userId, userName, chatId]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{ 
      width: "100%", maxWidth: "500px", height: "70vh", margin: "20px auto",
      background: "white", borderRadius: "15px", boxShadow: "0 10px 30px rgba(0,0,0,0.1)"
    }}>
      <div style={{
        padding: "20px", background: "#075E54", color: "white", 
        borderRadius: "15px 15px 0 0", textAlign: "center"
      }}>
        <h2 style={{ margin: 0 }}>ðŸ’¬ MIST Chat</h2>
        <div style={{ fontSize: "12px" }}>
          {allUserChats.length} personal chats saved âœ“
        </div>
      </div>

      <div style={{ flex: 1, overflowY: "auto", padding: "20px", backgroundColor: "#E5DDD5" }}>
        {messages.length === 0 ? (
          <div style={{ textAlign: "center", color: "#667781", paddingTop: "50px" }}>
            <div style={{ fontSize: "48px" }}>ðŸ’¬</div>
            <div>Your messages are saved forever!</div>
          </div>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{
              display: "flex", justifyContent: m.senderId === userId ? "flex-end" : "flex-start",
              marginBottom: "10px"
            }}>
              <div style={{
                maxWidth: "75%", padding: "10px 15px", borderRadius: "10px",
                background: m.senderId === userId ? "#DCF8C5" : "white",
                boxShadow: "0 1px 2px rgba(0,0,0,0.1)"
              }}>
                <div style={{ fontSize: "12px", opacity: 0.8 }}>
                  {m.senderName}
                </div>
                <div style={{ fontSize: "15px", margin: "2px 0" }}>
                  {m.message}
                </div>
                <div style={{ fontSize: "11px", opacity: 0.6 }}>
                  {m.timestamp.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'})}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ padding: "15px 20px", backgroundColor: "#F0F0F0", display: "flex", gap: "10px" }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type message..."
          style={{
            flex: 1, padding: "12px 16px", border: "1px solid #DDD",
            borderRadius: "25px", outline: "none", fontSize: "16px"
          }}
        />
        <button onClick={sendMessage} style={{
          width: "48px", height: "48px", borderRadius: "50%",
          background: "#00D15F", color: "white", border: "none", cursor: "pointer"
        }}>
          âž¤
        </button>
      </div>
    </div>
  );
}

export default Chat;
