import { useEffect, useState, useRef, useCallback } from "react";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp } from "firebase/firestore";
import { db } from "./firebase";

function Chat({ chatId = "global-chat", userId, userName = "User" }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Perfect persistence - loads ALL messages
  useEffect(() => {
    console.log("ðŸ”— Connecting to MIST Chat:", chatId);
    
    const q = query(
      collection(db, "chats", chatId, "messages"), 
      orderBy("timestamp")
    );
    
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const loadedMessages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        timestamp: doc.data().timestamp?.toDate() || new Date()
      }));
      
      console.log("ðŸ’¾ LOADED", loadedMessages.length, "MIST messages");
      setMessages(loadedMessages);
    });
    
    return unsubscribe;
  }, [chatId]);

  const sendMessage = useCallback(async () => {
    const trimmedText = text.trim();
    if (!trimmedText || !userId) return;

    // Instant MIST UI feedback
    const optimisticId = `mist-${Date.now()}`;
    setMessages(prev => [{
      id: optimisticId,
      senderId: userId,
      senderName: userName,
      message: trimmedText,
      timestamp: new Date()
    }, ...prev]);
    
    setText("");

    try {
      await addDoc(collection(db, "chats", chatId, "messages"), {
        senderId: userId,
        senderName: userName,
        message: trimmedText,
        timestamp: serverTimestamp()
      });
      console.log("âœ… MIST message SAVED forever");
    } catch (error) {
      console.error("âŒ Save failed:", error);
      setMessages(prev => prev.filter(m => m.id !== optimisticId));
      setText(trimmedText);
    }
  }, [text, userId, userName, chatId]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{ 
      width: "100%", maxWidth: "500px", height: "70vh", margin: "0 auto",
      background: "white", borderRadius: "20px", boxShadow: "0 15px 35px rgba(255,78,80,0.15)",
      display: "flex", flexDirection: "column", overflow: "hidden",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      {/* MIST Header - Matches App.js */}
      <div style={{
        padding: "20px",
        background: "linear-gradient(135deg, #FF4E50 0%, #F9D423 100%)",
        color: "white",
        textAlign: "center",
        boxShadow: "0 4px 15px rgba(255,78,80,0.3)"
      }}>
        <h2 style={{ 
          margin: 0, 
          fontSize: "1.5em", 
          fontWeight: "700",
          display: "flex", 
          alignItems: "center", 
          justifyContent: "center",
          gap: "12px"
        }}>
          ðŸ’¬ MIST Group Chat
        </h2>
        <div style={{ 
          fontSize: "13px", 
          opacity: 0.95, 
          fontWeight: "500",
          marginTop: "4px"
        }}>
          Messages saved forever âœ¨
        </div>
      </div>

      {/* Messages Area */}
      <div style={{ 
        flex: 1, 
        overflowY: "auto", 
        padding: "25px 20px",
        background: "linear-gradient(145deg, #f8f9ff 0%, #FFDEE9 100%)"
      }}>
        {messages.length === 0 ? (
          <div style={{
            display: "flex", 
            flexDirection: "column", 
            justifyContent: "center", 
            alignItems: "center", 
            height: "100%", 
            color: "#888",
            textAlign: "center"
          }}>
            <div style={{ fontSize: "55px", marginBottom: "20px" }}>ðŸ’¬</div>
            <div style={{ fontSize: "18px", fontWeight: "500", marginBottom: "8px" }}>
              Welcome to MIST Chat!
            </div>
            <div style={{ fontSize: "14px", opacity: 0.8 }}>
              Your messages are saved permanently
            </div>
          </div>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{
              display: "flex", 
              justifyContent: m.senderId === userId ? "flex-end" : "flex-start",
              marginBottom: "18px",
              animation: "slideInUp 0.3s ease"
            }}>
              <div style={{
                maxWidth: "72%",
                padding: "14px 18px",
                borderRadius: "22px",
                background: m.senderId === userId 
                  ? "linear-gradient(135deg, #FF4E50 0%, #F9D423 100%)" 
                  : "rgba(255,255,255,0.9)",
                color: m.senderId === userId ? "white" : "#333",
                boxShadow: m.senderId === userId 
                  ? "0 6px 20px rgba(255,78,80,0.4)" 
                  : "0 4px 12px rgba(0,0,0,0.08)",
                backdropFilter: "blur(10px)",
                border: m.senderId === userId ? "none" : "1px solid rgba(255,255,255,0.6)"
              }}>
                <div style={{ 
                  fontSize: "13px", 
                  fontWeight: "600", 
                  marginBottom: "6px",
                  opacity: m.senderId === userId ? 0.95 : 1
                }}>
                  {m.senderName?.slice(0,20) || "Student"}
                </div>
                <div style={{ 
                  fontSize: "15px", 
                  lineHeight: "1.4", 
                  wordBreak: "break-word" 
                }}>
                  {m.message}
                </div>
                <div style={{ 
                  fontSize: "12px", 
                  opacity: 0.8, 
                  marginTop: "8px",
                  textAlign: m.senderId === userId ? "right" : "left",
                  fontWeight: "500"
                }}>
                  {m.timestamp.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'})}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* MIST Input - Matches App.js gradient */}
      <div style={{
        padding: "22px 25px",
        background: "rgba(255,255,255,0.95)",
        borderTop: "1px solid rgba(255,78,80,0.2)",
        backdropFilter: "blur(15px)",
        display: "flex",
        gap: "15px",
        alignItems: "center"
      }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          style={{
            flex: 1,
            padding: "15px 22px",
            border: "2px solid rgba(255,78,80,0.2)",
            borderRadius: "30px",
            outline: "none",
            fontSize: "16px",
            background: "rgba(255,255,255,0.9)",
            backdropFilter: "blur(10px)",
            transition: "all 0.3s ease",
            fontWeight: "500"
          }}
          onFocus={(e) => {
            e.target.style.borderColor = "#FF4E50";
            e.target.style.boxShadow = "0 0 0 3px rgba(255,78,80,0.1)";
          }}
          onBlur={(e) => {
            e.target.style.borderColor = "rgba(255,78,80,0.2)";
            e.target.style.boxShadow = "none";
          }}
        />
        <button 
          onClick={sendMessage}
          disabled={!text.trim()}
          style={{
            width: "55px",
            height: "55px",
            borderRadius: "50%",
            background: "linear-gradient(135deg, #FF4E50 0%, #F9D423 100%)",
            color: "white",
            border: "none",
            cursor: text.trim() ? "pointer" : "not-allowed",
            fontSize: "20px",
            fontWeight: "bold",
            boxShadow: "0 6px 20px rgba(255,78,80,0.4)",
            transition: "all 0.3s ease",
            opacity: text.trim() ? 1 : 0.6
          }}
          onMouseOver={(e) => {
            if (text.trim()) {
              e.target.style.transform = "translateY(-3px)";
              e.target.style.boxShadow = "0 8px 25px rgba(255,78,80,0.5)";
            }
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "translateY(0)";
            e.target.style.boxShadow = "0 6px 20px rgba(255,78,80,0.4)";
          }}
        >
          âž¤
        </button>
      </div>

      <style>{`
        @keyframes slideInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
}

export default Chat;
