useEffect(() => {
  const STUDENT_NAME = "Student One";

  axios
    .get("http://localhost:5000/student/dashboard", {
      params: { student: STUDENT_NAME },
    })
    .then((res) => {
      console.log("✅ dashboard data:", res.data);
      setData(res.data);
      setLoading(false);
    })
    .catch((err) => {
      console.error("❌ dashboard error:", err.message);
      setLoading(false);
    });

  const interval = setInterval(() => {
    axios
      .get(
        `http://localhost:5000/student/request-status/${encodeURIComponent(
          STUDENT_NAME
        )}`
      )
      .then((res) => {
        if (res.data?.status) {
          setRequestStatus(res.data.status);
        }
      })
      .catch((err) => {
        console.error("status error:", err.message);
      });
  }, 3000);

  return () => clearInterval(interval);
}, []);
