import { useEffect, useState } from "react";
import axios from "axios";

export default function Student() {
  const [data, setData] = useState(null);
  const [requestStatus, setRequestStatus] = useState("No Request");
  const [loading, setLoading] = useState(true);

  const STUDENT_NAME = "Student One";

  useEffect(() => {
    // Initial dashboard data
    axios
      .get("http://localhost:5000/student/dashboard", {
        params: { student: STUDENT_NAME },
      })
      .then((res) => {
        setData(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));

    // Autoâ€‘refresh request status every 3 seconds
    const interval = setInterval(() => {
      axios
        .get(
          `http://localhost:5000/student/request-status/${encodeURIComponent(
            STUDENT_NAME
          )}`
        )
        .then((res) => {
          if (res.data?.status) {
            setRequestStatus(res.data.status);
          }
        })
        .catch(() => {});
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const requestMentor = () => {
    axios
      .post("http://localhost:5000/student/request", {
        student: STUDENT_NAME,
      })
      .then(() => setRequestStatus("Pending"));
  };

  const resetRequest = () => {
    axios
      .post("http://localhost:5000/student/reset-request", {
        student: STUDENT_NAME,
      })
      .then(() => setRequestStatus("No Request"));
  };

  if (loading || !data) {
    return (
      <div
        style={{
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          background: "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)",
          fontSize: "18px",
          color: "#4b5563",
        }}
      >
        Loading Student Dashboard...
      </div>
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        paddingTop: "80px",
        background: "linear-gradient(135deg, #FFDEE9 0%, #B5FFFC 100%)",
        display: "flex",
        justifyContent: "center",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "450px",
          background: "white",
          borderRadius: "20px",
          boxShadow: "0 15px 35px rgba(15,23,42,0.12)",
          padding: "22px 24px 28px",
        }}
      >
        <h2
          style={{
            margin: 0,
            marginBottom: "6px",
            fontSize: "1.6rem",
            color: "#4f46e5",
          }}
        >
          ðŸŽ“ Student Dashboard
        </h2>
        <p
          style={{
            margin: 0,
            marginBottom: "16px",
            fontSize: "0.9rem",
            color: "#6b7280",
          }}
        >
          Welcome, {data.name}
        </p>

        <div
          style={{
            padding: "10px 12px",
            borderRadius: "12px",
            background: "#f9fafb",
            marginBottom: "14px",
          }}
        >
          <p style={{ margin: 0, fontSize: "0.95rem", color: "#111827" }}>
            <b>Name:</b> {data.name}
          </p>
          <p style={{ margin: "4px 0 0", fontSize: "0.95rem", color: "#111827" }}>
            <b>Team:</b> {data.team}
          </p>
        </div>

        <h3
          style={{
            marginTop: "10px",
            marginBottom: "4px",
            fontSize: "1rem",
            color: "#111827",
          }}
        >
          Assigned Mentor
        </h3>
        <p style={{ marginTop: 0, marginBottom: "12px", color: "#4b5563" }}>
          {data.mentor || "Not assigned"}
        </p>

        <h3
          style={{
            marginTop: "10px",
            marginBottom: "4px",
            fontSize: "1rem",
            color: "#111827",
          }}
        >
          Mentor Interaction Status
        </h3>
        <p
          style={{
            marginTop: 0,
            marginBottom: "10px",
            fontWeight: "600",
            color:
              requestStatus === "Approved"
                ? "#16a34a"
                : requestStatus === "Rejected"
                ? "#dc2626"
                : requestStatus === "Pending"
                ? "#d97706"
                : "#4b5563",
          }}
        >
          {requestStatus}
        </p>

        {requestStatus === "No Request" && (
          <button
            onClick={requestMentor}
            style={{
              marginTop: "8px",
              padding: "10px 16px",
              background:
                "linear-gradient(135deg, #4f46e5 0%, #6366f1 100%)",
              color: "white",
              border: "none",
              borderRadius: "999px",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "0.95rem",
              width: "100%",
            }}
          >
            Request Mentor Interaction
          </button>
        )}

        {requestStatus === "Rejected" && (
          <button
            onClick={resetRequest}
            style={{
              marginTop: "8px",
              padding: "10px 16px",
              background:
                "linear-gradient(135deg, #dc2626 0%, #f97373 100%)",
              color: "white",
              border: "none",
              borderRadius: "999px",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "0.95rem",
              width: "100%",
            }}
          >
            Request Again
          </button>
        )}
      </div>
    </div>
  );
}
