import { useEffect, useState, useRef, useCallback } from "react";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp } from "firebase/firestore";
import { db } from "./firebase";

function Chat({ chatId = "global-chat", userId, userName = "User" }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ðŸ”¥ PERFECT PERSISTENCE - Simple ascending order (oldest first)
  useEffect(() => {
    console.log("ðŸ”— Connecting to chat:", chatId);
    
    const q = query(
      collection(db, "chats", chatId, "messages"), 
      orderBy("timestamp") // Simple ascending - NEVER fails
    );
    
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const loadedMessages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        timestamp: doc.data().timestamp?.toDate() || new Date()
      }));
      
      console.log("ðŸ’¾ LOADED", loadedMessages.length, "messages on refresh");
      setMessages(loadedMessages);
    });
    
    return unsubscribe;
  }, [chatId]);

  const sendMessage = useCallback(async () => {
    const trimmedText = text.trim();
    if (!trimmedText || !userId) return;

    // Instant UI feedback
    const optimisticId = `temp-${Date.now()}`;
    setMessages(prev => [{
      id: optimisticId,
      senderId: userId,
      senderName: userName,
      message: trimmedText,
      timestamp: new Date()
    }, ...prev]);
    
    setText("");

    // Permanent save to Firestore
    try {
      await addDoc(collection(db, "chats", chatId, "messages"), {
        senderId: userId,
        senderName: userName,
        message: trimmedText,
        timestamp: serverTimestamp()
      });
      console.log("âœ… Message SAVED FOREVER");
    } catch (error) {
      console.error("âŒ Save failed:", error);
      // Remove failed optimistic message
      setMessages(prev => prev.filter(m => m.id !== optimisticId));
      setText(trimmedText);
    }
  }, [text, userId, userName, chatId]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{ 
      width: "100%", maxWidth: "500px", height: "70vh", margin: "20px auto",
      background: "white", borderRadius: "15px", boxShadow: "0 10px 30px rgba(0,0,0,0.1)",
      display: "flex", flexDirection: "column"
    }}>
      {/* Header */}
      <div style={{
        padding: "20px", background: "#1457cce3",
        color: "white", borderRadius: "15px 15px 0 0", textAlign: "center"
      }}>
        <h2 style={{ margin: 0, fontSize: "1.4em" }}>ðŸ’¬ MIST Chat</h2>
        <div style={{ fontSize: "12px", opacity: 0.9 }}>
          Messages saved forever like WhatsApp
        </div>
      </div>

      {/* Messages */}
      <div style={{ 
        flex: 1, overflowY: "auto", padding: "20px", 
        backgroundColor: "#d8daddff"
      }}>
        {messages.length === 0 ? (
          <div style={{
            display: "flex", flexDirection: "column", justifyContent: "center", 
            alignItems: "center", height: "100%", color: "#667781"
          }}>
            <div style={{ fontSize: "48px", marginBottom: "15px" }}>ðŸ’¬</div>
            <div style={{ fontSize: "16px", marginBottom: "5px" }}>No messages yet</div>
            <div style={{ fontSize: "14px", opacity: 0.8 }}>Send one to start chatting!</div>
          </div>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{
              display: "flex", 
              justifyContent: m.senderId === userId ? "flex-end" : "flex-start",
              marginBottom: "8px"
            }}>
              <div style={{
                maxWidth: "75%", padding: "8px 12px", borderRadius: "7.5px",
                background: m.senderId === userId ? "#DCF8C5" : "#FFFFFF",
                boxShadow: "0 1px 0.5px rgba(0,0,0,0.13)"
              }}>
                <div style={{ 
                  fontSize: "12px", opacity: 0.8, marginBottom: "2px",
                  fontWeight: m.senderId === userId ? "normal" : "500"
                }}>
                  {m.senderName?.slice(0,15) || "User"}
                </div>
                <div style={{ fontSize: "14px", lineHeight: "1.3", wordBreak: "break-word" }}>
                  {m.message}
                </div>
                <div style={{ 
                  fontSize: "11px", opacity: 0.6, marginTop: "4px",
                  textAlign: m.senderId === userId ? "right" : "left"
                }}>
                  {m.timestamp.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'})}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div style={{
        padding: "15px 20px", backgroundColor: "#F0F0F0",
        borderTop: "1px solid #E1E1E1", display: "flex", gap: "10px"
      }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message"
          style={{
            flex: 1, padding: "12px 18px", border: "1px solid #D9D9D9",
            borderRadius: "21px", outline: "none", fontSize: "16px",
            backgroundColor: "white"
          }}
        />
        <button 
          onClick={sendMessage}
          style={{
            width: "45px", height: "45px", borderRadius: "50%",
            background: "#007ad1ff", color: "white", border: "none",
            cursor: "pointer", fontSize: "18px", display: "flex",
            alignItems: "center", justifyContent: "center"
          }}
        >
          âž¤
        </button>
      </div>
    </div>
  );
}

export default Chat;
