// App.jsx
import { useEffect, useState } from "react";
import { signInWithPopup, signOut } from "firebase/auth";
import { auth, googleProvider } from "./firebase";
import Chat from "./Chat";

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsub = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
    return unsub;
  }, []);

  const login = () => signInWithPopup(auth, googleProvider);
  const logout = () => signOut(auth);

  if (loading) return <div>Loading...</div>;

  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "30px 10px",
        background: "#f0f2f5",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <h1 style={{ color: "#8B5CF6", marginBottom: "10px" }}>ðŸš€ MIST CHAT</h1>

      {!user ? (
        <button
          onClick={login}
          style={{
            padding: "14px 28px",
            background: "#8B5CF6",
            color: "white",
            border: "none",
            borderRadius: "25px",
            fontSize: "16px",
            cursor: "pointer",
          }}
        >
          Login with Google
        </button>
      ) : (
        <>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "10px",
              marginBottom: "15px",
            }}
          >
            {user.photoURL && (
              <img
                src={user.photoURL}
                alt="avatar"
                style={{ width: 40, height: 40, borderRadius: "50%" }}
              />
            )}
            <div>
              <div style={{ fontWeight: "600" }}>{user.displayName}</div>
              <div style={{ fontSize: 12, color: "#555" }}>{user.email}</div>
            </div>
            <button
              onClick={logout}
              style={{
                marginLeft: "20px",
                padding: "8px 18px",
                borderRadius: "20px",
                border: "none",
                background: "#ef4444",
                color: "white",
                cursor: "pointer",
              }}
            >
              Logout
            </button>
          </div>

          {/* Single shared room for everyone */}
          <Chat
            chatId="chat-7077a"
            userId={user.uid}
            userName={user.displayName || "User"}
          />
        </>
      )}
    </div>
  );
}

export default App;
