import { useEffect, useState, useRef, useCallback } from "react";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp, getDocs } from "firebase/firestore";
import { db } from "./firebase";

function Chat({ chatId = "global-chat", userId, userName = "User" }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [debugInfo, setDebugInfo] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ðŸ”¥ DEBUG: Check if path exists on mount
  useEffect(() => {
    const checkPath = async () => {
      try {
        const path = `chats/${chatId}/messages`;
        console.log("ðŸ” Checking path:", path);
        const snapshot = await getDocs(collection(db, "chats", chatId, "messages"));
        console.log("ðŸ“ Found", snapshot.docs.length, "messages in", path);
        setDebugInfo(`${snapshot.docs.length} messages found`);
      } catch (error) {
        console.error("âŒ Path error:", error);
        setDebugInfo("âŒ Path doesn't exist - create chats/global-chat/messages");
      }
    };
    checkPath();
  }, [chatId]);

  // Load messages
  useEffect(() => {
    console.log("ðŸ”— Listening to:", `chats/${chatId}/messages`);
    
    const q = query(
      collection(db, "chats", chatId, "messages"), 
      orderBy("timestamp")
    );
    
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const loadedMessages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        timestamp: doc.data().timestamp?.toDate() || new Date()
      }));
      
      console.log("ðŸ’¾ LOADED", loadedMessages.length, "messages");
      setMessages(loadedMessages);
      setDebugInfo(`Loaded ${loadedMessages.length} messages âœ“`);
    }, (error) => {
      console.error("âŒ Listener failed:", error);
      setDebugInfo(`Listener error: ${error.message}`);
    });
    
    return unsubscribe;
  }, [chatId]);

  const sendMessage = useCallback(async () => {
    const trimmedText = text.trim();
    if (!trimmedText || !userId) {
      console.log("âŒ Empty message or no userId");
      return;
    }

    console.log("ðŸš€ Sending:", trimmedText, "to", chatId);

    // Instant UI
    const optimisticId = `temp-${Date.now()}`;
    setMessages(prev => [{
      id: optimisticId,
      senderId: userId,
      senderName: userName,
      message: trimmedText,
      timestamp: new Date()
    }, ...prev]);
    
    setText("");

    // FORCE SAVE with error handling
    try {
      const docRef = await addDoc(collection(db, "chats", chatId, "messages"), {
        senderId: userId,
        senderName: userName,
        message: trimmedText,
        timestamp: serverTimestamp()
      });
      
      console.log("âœ… SAVED doc ID:", docRef.id);
      setDebugInfo(`Saved message ${docRef.id.slice(0,8)} âœ“`);
    } catch (error) {
      console.error("âŒ CRITICAL SAVE ERROR:", error);
      setDebugInfo(`âŒ Save failed: ${error.message}`);
      // Remove failed optimistic message
      setMessages(prev => prev.filter(m => m.id !== optimisticId));
      setText(trimmedText);
    }
  }, [text, userId, userName, chatId]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{ 
      width: "100%", maxWidth: "500px", height: "70vh", margin: "20px auto",
      background: "white", borderRadius: "15px", boxShadow: "0 10px 30px rgba(0,0,0,0.1)",
      display: "flex", flexDirection: "column"
    }}>
      <div style={{
        padding: "15px", background: "#075E54", color: "white", 
        borderRadius: "15px 15px 0 0", textAlign: "center"
      }}>
        <h2 style={{ margin: "0 0 5px 0", fontSize: "1.4em" }}>ðŸ’¬ MIST Chat</h2>
        <div style={{ fontSize: "11px", opacity: 0.9 }}>
          Debug: {debugInfo} | Chat: {chatId}
        </div>
      </div>

      <div style={{ flex: 1, overflowY: "auto", padding: "20px", backgroundColor: "#E5DDD5" }}>
        {messages.length === 0 ? (
          <div style={{
            display: "flex", flexDirection: "column", justifyContent: "center", 
            alignItems: "center", height: "100%", color: "#667781"
          }}>
            <div style={{ fontSize: "48px", marginBottom: "15px" }}>ðŸ’¬</div>
            <div style={{ fontSize: "16px" }}>No messages yet</div>
            <div style={{ fontSize: "12px", opacity: 0.8, marginTop: "5px" }}>
              Check console (F12) for debug info
            </div>
          </div>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{
              display: "flex", 
              justifyContent: m.senderId === userId ? "flex-end" : "flex-start",
              marginBottom: "10px"
            }}>
              <div style={{
                maxWidth: "75%", padding: "10px 15px", borderRadius: "10px",
                background: m.senderId === userId ? "#DCF8C5" : "white",
                boxShadow: "0 1px 2px rgba(0,0,0,0.1)"
              }}>
                <div style={{ fontSize: "12px", opacity: 0.8 }}>
                  {m.senderName?.slice(0,15) || "User"}
                </div>
                <div style={{ fontSize: "15px", margin: "2px 0" }}>
                  {m.message}
                </div>
                <div style={{ fontSize: "11px", opacity: 0.6 }}>
                  {m.timestamp.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'})}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <div style={{
        padding: "15px 20px", backgroundColor: "#F0F0F0",
        borderTop: "1px solid #E1E1E1", display: "flex", gap: "10px"
      }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type message..."
          style={{
            flex: 1, padding: "12px 16px", border: "1px solid #DDD",
            borderRadius: "25px", outline: "none", fontSize: "16px"
          }}
        />
        <button onClick={sendMessage} style={{
          width: "48px", height: "48px", borderRadius: "50%",
          background: "#00D15F", color: "white", border: "none", cursor: "pointer"
        }}>
          âž¤
        </button>
      </div>
    </div>
  );
}

export default Chat;
