import { useEffect, useState, useRef, useCallback } from "react";
import { collection, addDoc, query, orderBy, onSnapshot, serverTimestamp, limit } from "firebase/firestore";
import { db } from "./firebase";

function Chat({ chatId = "global-chat", userId, userName = "User" }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ðŸ”¥ WHATSAPP-STYLE PERSISTENCE - Loads ALL messages forever
  useEffect(() => {
    console.log("ðŸ”— Connecting to WhatsApp-style chat:", chatId);
    
    // Load LAST 50 messages (WhatsApp style - most recent first)
    const q = query(
      collection(db, "chats", chatId, "messages"), 
      orderBy("timestamp", "desc"),
      limit(50)
    );
    
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const msgs = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        timestamp: doc.data().timestamp?.toDate() || new Date()
      })).reverse(); // Show newest at bottom (WhatsApp style)
      
      setMessages(msgs);
      console.log("ðŸ’¾ Loaded", msgs.length, "persistent messages");
    });
    
    return unsubscribe;
  }, [chatId]);

  // ðŸ”¥ INSTANT SEND + PERMANENT SAVE
  const sendMessage = useCallback(async () => {
    const trimmedText = text.trim();
    if (!trimmedText || !userId) return;

    // 1. INSTANT UI (WhatsApp feel)
    const optimisticId = `temp-${Date.now()}`;
    const optimisticMsg = {
      id: optimisticId,
      senderId: userId,
      senderName: userName,
      message: trimmedText,
      timestamp: new Date()
    };
    setMessages(prev => [optimisticMsg, ...prev]);
    setText("");

    // 2. PERMANENT SAVE (WhatsApp backend)
    try {
      await addDoc(collection(db, "chats", chatId, "messages"), {
        senderId: userId,
        senderName: userName,
        message: trimmedText,
        timestamp: serverTimestamp()
      });
      console.log("âœ… SAVED PERMANENTLY like WhatsApp");
    } catch (error) {
      console.error("âŒ Save failed:", error);
      setMessages(prev => prev.filter(m => m.id !== optimisticId));
      setText(trimmedText);
    }
  }, [text, userId, userName, chatId]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{ 
      width: "100%", maxWidth: "500px", height: "70vh", margin: "20px auto",
      background: "white", borderRadius: "15px", boxShadow: "0 10px 30px rgba(0,0,0,0.1)",
      display: "flex", flexDirection: "column", fontFamily: "'Segoe UI', sans-serif"
    }}>
      {/* WhatsApp-style Header */}
      <div style={{
        padding: "20px", 
        background: "linear-gradient(135deg, #25D366 0%, #128C7E 100%)",
        color: "white", borderRadius: "15px 15px 0 0", textAlign: "center"
      }}>
        <h2 style={{ margin: 0, fontSize: "1.5em" }}>ðŸ’¬ MIST Chat</h2>
        <div style={{ fontSize: "12px", opacity: 0.9 }}>
          Messages saved forever âœ“ {userId?.slice(0,8)}
        </div>
      </div>

      {/* WhatsApp-style Messages */}
      <div style={{ 
        flex: 1, overflowY: "auto", padding: "20px", 
        backgroundColor: "#EDEDED", backgroundImage: "url('data:image/svg+xml;base64,...')"
      }}>
        {messages.length === 0 ? (
          <div style={{
            display: "flex", flexDirection: "column", justifyContent: "center", 
            alignItems: "center", height: "100%", color: "#666", textAlign: "center"
          }}>
            <div style={{ fontSize: "48px", marginBottom: "10px" }}>ðŸ’¬</div>
            <div style={{ fontSize: "18px", marginBottom: "5px" }}>Start a conversation</div>
            <div style={{ fontSize: "14px", opacity: 0.7 }}>Messages are saved permanently</div>
          </div>
        ) : (
          messages.map((m) => (
            <div key={m.id} style={{
              display: "flex", 
              justifyContent: m.senderId === userId ? "flex-end" : "flex-start",
              marginBottom: "12px"
            }}>
              <div style={{
                maxWidth: "70%", padding: "10px 14px", borderRadius: "18px",
                background: m.senderId === userId 
                  ? "#DCF8C6" 
                  : "#FFFFFF",
                color: "#000",
                boxShadow: "0 1px 2px rgba(0,0,0,0.1)"
              }}>
                <div style={{ fontSize: "0.85em", opacity: 0.8 }}>
                  {m.senderName || "User"}
                </div>
                <div style={{ fontSize: "1em", margin: "2px 0", wordBreak: "break-word" }}>
                  {m.message}
                </div>
                <div style={{ 
                  fontSize: "0.7em", opacity: 0.6, 
                  textAlign: m.senderId === userId ? "right" : "left"
                }}>
                  {m.timestamp.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'})}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* WhatsApp-style Input */}
      <div style={{
        padding: "15px 20px", borderTop: "1px solid #E1E1E1", 
        backgroundColor: "#F0F0F0", display: "flex", gap: "10px"
      }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message"
          style={{
            flex: 1, padding: "12px 16px", border: "1px solid #C1C1C1", 
            borderRadius: "25px", outline: "none", fontSize: "16px",
            backgroundColor: "white"
          }}
        />
        <button onClick={sendMessage} style={{
          width: "50px", height: "50px", borderRadius: "50%",
          background: "#25D366", color: "white", border: "none", 
          cursor: "pointer", fontSize: "20px", display: "flex",
          alignItems: "center", justifyContent: "center"
        }}>
          âž¤
        </button>
      </div>
    </div>
  );
}

export default Chat;
